import React from 'react';
import { Link } from 'react-router-dom';
const SubjectList = ({ subjects, onSelectSubject }) => {
    return (
        <div className='text-center mt-4'>
            <div className='d-flex gap-4 flex-wrap justify-content-center'>
            {subjects.map((subject) => (
            <div class="card" style={{width:"18rem"}}>
                <img class="card-img-top" src={subject.link} alt="Card image cap"/>
                    <div class="card-body">
                        <h5 class="card-title">{subject.name}</h5>
                        <div className="btn-group" role="group">
                <Link to={`/quiz/${subject.id}`} className="btn btn-primary">
                  Take Quiz
                </Link>
                <Link to={`/tutorials/${subject.id}`} className="btn btn-success">
                  See Tutorials
                </Link>
              </div>
                    </div>
            </div>))}
            </div>
            
        </div>
    );
};

export default SubjectList;
