// QuizPage.js

import React, { useState } from 'react';

const QuizPage = ({ questions, onQuizSubmit }) => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOptions, setSelectedOptions] = useState({});

  const handleOptionChange = (questionId, option) => {
    setSelectedOptions({
      ...selectedOptions,
      [questionId]: option,
    });
    
  };

  const handleNextQuestion = () => {
    setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
  };

  const isLastQuestion = currentQuestionIndex === questions.length - 1;

  return (
    <div>
      <h2>Quiz</h2>
      {currentQuestionIndex < questions.length && (
        <div>
          <p>{questions[currentQuestionIndex].text}</p>
          {questions[currentQuestionIndex].options.map((option) => (
            <button className='m-2 btn btn-success col-6 p-3' key={option} className="option-label" onClick={() => handleOptionChange(questions[currentQuestionIndex].id, option)}>
              
                {questions[currentQuestionIndex].option}
                {/* value={option} */}
              {option}
            </button>
          ))}
          <button type="button" onClick={handleNextQuestion}>
            {isLastQuestion ? 'Submit' : 'Next'}
          </button>
        </div>
      )}
      {currentQuestionIndex === questions.length && (
        <form onSubmit={(e) => onQuizSubmit(e, selectedOptions)}>
          <button type="submit">Submit</button>
        </form>
      )}
    </div>
  );
};

export default QuizPage;
