// App.js

import React, { useState } from 'react';
import { BrowserRouter as Router} from 'react-router-dom';
import { Switch, Route } from 'react-router-dom';
import SubjectList from './components/SubjectList';
import QuizPage from './components/QuizPage';
import TutorialPage from './components/TutorialPage';
import subjectsData from './data/subjects';

const App = () => {
  const [selectedSubject, setSelectedSubject] = useState(null);

  const handleSelectSubject = (subjectId) => {
    // Find the selected subject based on subjectId
    const subject = subjectsData.find((subj) => subj.id === subjectId);
    setSelectedSubject(subject);
  };

  return (
    <Router>
      <div className="app-container">
        <Switch>
          <Route path="/" exact>
            {!selectedSubject && (
              <SubjectList subjects={subjectsData} onSelectSubject={handleSelectSubject} />
            )}
          </Route>
          <Route path="/quiz/:subjectId">
            {selectedSubject && <QuizPage selectedSubject={selectedSubject} />}
          </Route>
          <Route path="/tutorials/:subjectId">
            {selectedSubject && <TutorialPage selectedSubject={selectedSubject} />}
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
