// TutorialPage.js

import React from 'react';

const TutorialPage = ({ selectedSubject, tutorialData }) => {
  // Check if tutorialData is undefined or empty
  if (!tutorialData || tutorialData.length === 0) {
    return <div>No tutorial data available.</div>;
  }

  // Find the subject data
  const subjectData = tutorialData.find((data) => data.subjectId === selectedSubject.id);

  // Check if subjectData is undefined
  if (!subjectData) {
    return <div>No data available for this subject.</div>;
  }

  return (
    <div>
      <h2>{selectedSubject.name} Tutorials</h2>

      <div>
        <h3>Videos</h3>
        <ul>
          {subjectData.videos.map((video) => (
            <li key={video.id}>
              <a href={video.link} target="_blank" rel="noopener noreferrer">
                {video.title}
              </a>
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h3>Articles</h3>
        <ul>
          {subjectData.articles.map((article) => (
            <li key={article.id}>
              <a href={article.link} target="_blank" rel="noopener noreferrer">
                {article.title}
              </a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default TutorialPage;
