// SubjectList.js

import React from 'react';
import { Link } from 'react-router-dom';

const SubjectList = ({ subjects, onSelectSubject }) => {
  return (
    <div className='text-center mt-4'>
      <h2 style={{ fontSize: "5vw" }}>Choose a Subject</h2>
      <div className='d-flex gap-4 flex-wrap'>
        {subjects.map((subject) => (
          <div className="card" style={{ width: "18rem" }} key={subject.id}>
            <img className="card-img-top" src={subject.link} alt={`Image for ${subject.name}`} />
            <div className="card-body">
              <h5 className="card-title">{subject.name}</h5>
              <div className="btn-group" role="group">
                <Link to={`/quiz/${subject.id}`} className="btn btn-primary">
                  Take Quiz
                </Link>
                <Link to={`/tutorials/${subject.id}`} className="btn btn-success">
                  See Tutorials
                </Link>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SubjectList;
