import React, { useState } from 'react';
import SubjectList from './components/SubjectList';
import TutorialPage from './components/TutorialPage';
import QuizPage from './components/QuizPage';
import QuizResult from './components/QuizResult';
import subjectsData from './data/subjects';
import './App.css'

const App = () => {
  const [selectedSubject, setSelectedSubject] = useState(null);
  const [quizResult, setQuizResult] = useState(null);

  const handleSelectSubject = (subject) => {
    setSelectedSubject(subject);
  };

  const handleQuizSubmit = (event, selectedOptions) => {
    event.preventDefault();
    // Calculate quiz result logic here
    // Set the result using setQuizResult
  };

  return (
    <div className="app-container">
      {!selectedSubject && (
        <SubjectList subjects={subjectsData} onSelectSubject={handleSelectSubject} />
      )}
      {selectedSubject && <TutorialPage selectedSubject={selectedSubject} />}
      {selectedSubject && (
        <QuizPage
          questions={[
            {
              id: 1,
              text: 'What does HTML stand for?',
              options: ['Hyper Text Markup Language', 'Hyperlink and Text Markup Language', 'HighText Machine Language', 'None of the above'],
              correctAnswer: 'Hyper Text Markup Language',
            },
            {
              id: 2,
              text: 'Which of the following is a CSS preprocessor?',
              options: ['Sass', 'LESS', 'Stylus', 'All of the above'],
              correctAnswer: 'All of the above',
            },
            {
              id: 3,
              text: 'What is the purpose of JavaScript?',
              options: ['To style web pages', 'To program the behavior of web pages', 'To create database queries', 'To define the structure of web pages'],
              correctAnswer: 'To program the behavior of web pages',
            },
            {
              id: 4,
              text: 'What is the output of the following JavaScript code?\n\n```javascript\nconsole.log(2 + "2");\n```',
              options: ['4', '22', 'ConcatenationError', 'NaN'],
              correctAnswer: '22',
            },
            {
              id: 5,
              text: 'Which of the following is not a valid React lifecycle method?',
              options: ['componentDidMount', 'componentShouldUpdate', 'componentWillUnmount', 'render'],
              correctAnswer: 'componentShouldUpdate',
            },
            // Add more questions as needed
          ]}
          onQuizSubmit={handleQuizSubmit}
        />
      )}
      {quizResult && <QuizResult result={quizResult} />}
    </div>
  );
};

export default App;
