import React from 'react';

const SubjectList = ({ subjects, onSelectSubject }) => {
  return (
    <div className='text-center mt-4'>
      <h2 style={{fontSize:"5vw"}}>Choose a Subject</h2>
      <ul>
        {subjects.map((subject) => (
          <li style={{cursor:"pointer", listStyle:"none",fontSize:"24px"}} key={subject.id} onClick={() => onSelectSubject(subject.id)}>
            {subject.name}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SubjectList;
