// SubjectList.js

import React from 'react';

const SubjectList = ({ subjects, onSelectSubject }) => {
  return (
    <div>
      <h2>Choose a Subject:</h2>
      <div className="card-deck">
        {subjects.map((subject, index) => (
          <div key={index} className="card" onClick={() => onSelectSubject(subject.name)}>
            <img src={`path/to/${subject.name.toLowerCase()}.jpg`} className="card-img-top" alt={subject.name} />
            <div className="card-body">
              <h5 className="card-title">{subject.name}</h5>
              {/* Add any additional information about the subject if needed */}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SubjectList;
