// App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch , Routes} from 'react-router-dom';
import SubjectList from './components/SubjectList';
import QuizPage from './components/QuizPage';
import TutorialPage from './components/TutorialPage';
import subjectsData from './data/subjects';

const App = () => {
  const [selectedSubject, setSelectedSubject] = useState(null);

  const handleSelectSubject = (subjectId) => {
    // Find the selected subject based on subjectId
    const subject = subjectsData.find((subj) => subj.id === subjectId);
    setSelectedSubject(subject);
  };

  return (
    <Router>
      <div className="app-container">
        {/* Use Routes instead of Switch */}
        <Routes>
          <Route path="/" element={<SubjectList subjects={subjectsData} onSelectSubject={handleSelectSubject} />} />
          <Route path="/quiz" element={selectedSubject && <QuizPage selectedSubject={selectedSubject} />} />
          <Route path="/tutorials/:subjectId" element={selectedSubject && <TutorialPage selectedSubject={selectedSubject} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
