import React from 'react';

const TutorialPage = ({ selectedSubject }) => {
  if (!selectedSubject) {
    return <div>No subject selected.</div>;
  }

  const { name, videos, articles } = selectedSubject;

  return (
    <div>
      <h2>{name} Tutorials</h2>

      <div>
        <h3>Videos</h3>
        <ul>
          {videos.map((video) => (
            <li key={video.id}>
              <strong>{video.title}</strong>: <a href={video.link} target="_blank" rel="noopener noreferrer">Watch Video</a>
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h3>Articles</h3>
        <ul>
          {articles.map((article) => (
            <li key={article.id}>
              <strong>{article.title}</strong>: <a href={article.link} target="_blank" rel="noopener noreferrer">Read Article</a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default TutorialPage;
